    private void btIngresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btIngresarActionPerformed
        
        int idLocal, usercodeLocal;
        idLocal = Integer.parseInt(tfID.getText());
        usercodeLocal = Integer.parseInt(tfUserCode.getText());
        
        controlador.permitido(idLocal, usercodeLocal, this);
        
    }//GEN-LAST:event_btIngresarActionPerformed

